import React from "react";
import "./Bauble.css";

class Bauble extends React.Component {
  state = { hoveredOn: false };

  getStyle = () => {
    if (!this.props.withBauble) {
      if (this.state.hoveredOn) {
        this.setState({ hoveredOn: false });
      } else {
        this.setState({ hoveredOn: true });
      }
    }
  };

  render() {
    return (
      <div
        className={this.props.withBauble && "baubles-bubble"}
        onMouseEnter={this.toggleHover}
        onMouseLeave={this.toggleHover}
      >
        <a
          href={this.props.referenceLink}
          target="_blank"
          rel="noopener noreferrer"
          className="baubles"
          style={this.getStyle()}
        >
          <ion-icon name={this.props.ionicon}></ion-icon>
        </a>
      </div>
    );
  }
}

Bauble.defaultProps = {
  referenceLink: "#",
  withBauble: false,
};

export default Bauble;
