import React from "react";
import OwlCarousel from "react-owl-carousel";
import EventCard from "../EventCard/EventCard";
import "owl.carousel/dist/assets/owl.carousel.css";
import "owl.carousel/dist/assets/owl.theme.default.css";
import "./SectionUpcomingEvents.css";

const eventCards = [{ eventType: "" }, { eventType: "" }];

const renderEventCards = eventCards.map((item) => {
  return (
    <div className="item">
      <EventCard eventType={item.eventType} />
    </div>
  );
});

class SectionUpcomingEvents extends React.Component {
  render() {
    return (
      <section className="section-upcoming-events">
        <h1>ORGANIZATION SPOTLIGHT</h1>
        <OwlCarousel>{this.renderEventCards()}</OwlCarousel>
      </section>
    );
  }
}

export default SectionUpcomingEvents;
