import React from "react";

const StatementCard = ({
  ionicon,
  header = "[Input Header]",
  text = "[Input Text]",
}) => {
  return (
    <div className="statement-card">
      <ionicons name={ionicon} />
      <h2>{header}</h2>
      <p>{text}</p>
    </div>
  );
};

export default StatementCard;
