import React from "react";
import "./NavigationItem.css";

class NavigationItem extends React.Component {
  state = { hoveredOn: false };

  toggleHover = (e) => {
    alert(`Before State is ${this.state.hoveredOn}`);
    this.setState({ hoveredOn: `${!this.state.hoveredOn}` });
    alert(`After State is ${this.state.hoveredOn}`);

    if (this.state.hoveredOn === true) {
      e.target.style.color = this.props.itemHoverColor;
    } else {
      e.target.style.color = this.props.itemColor;
    }
  };

  render() {
    return (
      <a
        href={this.props.source}
        style={{ color: this.props.itemColor }}
        onMouseEnter={this.toggleHover}
        onMouseLeave={this.toggleHover}
      >
        {this.props.itemName}
      </a>
    );
  }
}

NavigationItem.defaultProps = {
  itemColor: "White",
  itemHoverColor: "Blue",
  itemName: "Input Item Name",
  source: "#",
};

export default NavigationItem;
